import { array } from "./imm";
describe("array", function () {
    var arr = [1, 2, 3, 4, 5];
    test("insert", function () {
        expect(array.insert(arr, 0, 9, 8, 7)).toEqual([9, 8, 7, 1, 2, 3, 4, 5]);
    });
    test("append", function () {
        expect(array.append(arr, 1, 2, 3)).toEqual([1, 2, 3, 4, 5, 1, 2, 3]);
    });
    test("remove", function () {
        expect(array.remove(arr, 2)).toEqual([1, 3, 4, 5]);
        expect(array.remove(arr, function (x) { return x % 2 === 0; })).toEqual([1, 3, 5]);
    });
    test("set", function () {
        expect(array.set(arr, 0, 9)).toEqual([9, 2, 3, 4, 5]);
        expect(array.set(arr, arr.length - 1, function (x) { return x + 1; })).toEqual([
            1, 2, 3, 4, 6,
        ]);
    });
});
