import { objectMap } from "./std";
export function createCaseConverter(mapKey) {
    return function convertCase(obj) {
        if (obj && Object.getPrototypeOf(obj) === Object.prototype) {
            return objectMap(obj, convertCase, mapKey);
        }
        else if (Array.isArray(obj))
            return obj.map(convertCase);
        else
            return obj;
    };
}
export var snakeToCamelCase = createCaseConverter(function (k) {
    return k.replace(/_(\w)/, function (_, x) { return x.toUpperCase(); });
});
export var camelToSnakeCase = createCaseConverter(function (k) {
    return k.replace(/([A-Z])/, function (_, x) { return "_" + x.toLowerCase(); });
});
